SELECT ab.ACCOMMODATION_ADMIN_NO, ab.NAME, ab.ACCOMMODATION_TYPE, ab.ADDRESS, GROUP_CONCAT(DISTINCT r.CHECK_IN_TIME) AS CHECK_IN_TIMES, GROUP_CONCAT(DISTINCT f.FACILITY_NAME) AS FACILITY_NAMES, MIN(br.BASIC_RATE) AS BASIC_RATE FROM (SELECT a.ACCOMMODATION_ADMIN_NO, a.NAME, a.ACCOMMODATION_TYPE, a.ADDRESS FROM facilities f JOIN accommodation a ON a.ACCOMMODATION_ADMIN_NO = f.ACCOMMODATION_ADMIN_NO
WHERE f.FACILITY_NAME IN
#{options}
) ab JOIN room r ON ab.ACCOMMODATION_ADMIN_NO = r.ACCOMMODATION_ADMIN_NO JOIN basic_rate br ON r.ROOM_NO = br.ROOM_NO JOIN facilities f ON f.ACCOMMODATION_ADMIN_NO = ab.ACCOMMODATION_ADMIN_NO
WHERE ab.ACCOMMODATION_TYPE = #{searchDTO.type}
GROUP BY ab.ACCOMMODATION_ADMIN_NO, ab.NAME, ab.ACCOMMODATION_TYPE, ab.ADDRESS HAVING MIN(br.BASIC_RATE) BETWEEN #{searchDTO.minRate} AND #{searchDTO.maxRate} ORDER BY ab.ACCOMMODATION_ADMIN_NO LIMIT #{pi.boardLimit} OFFSET #{pi.offset}

