SELECT COUNT(DISTINCT ac.ACCOMMODATION_ADMIN_NO) FROM ACCOMMODATION ac JOIN ROOM r ON ac.ACCOMMODATION_ADMIN_NO = r.ACCOMMODATION_ADMIN_NO JOIN BASIC_RATE br ON r.ROOM_NO = br.ROOM_NO
